if(msg.topic != "validate"){
    return null;
}
var validationCheck = JSON.parse(flow.get("validation_file"));
var sensors = flow.get("discovered_sensors");
var sensor = sensors[msg.payload];

var sensorType = sensor.sensor_type;
var readings = sensor.sensor_data;

var validationStepsArray = validationCheck[sensorType.toString()];

var currentStepData = validationStepsArray[sensor.validation_step];
console.log("validation Step: "+sensor.validation_step);
var validated = true;
for(var variable in readings){
    if(variable == "instruction"){
        continue;
    }
    var validationObject = currentStepData[variable];
    console.log("validating: "+variable+" current value: "+readings[variable]+" high: "+validationObject["high"]+" low: "+validationObject["low"]);

    if(readings[variable]<validationObject["low"] || readings[variable]>validationObject["high"]){
        console.log("reading outside range");
        validated = false;
    }else{
        console.log("reading inside range");
    }
}

if(validated){
    sensor.validation_step++;
    flow.set("discovered_sensors", sensors);
    var sensorValidationObjects = flow.get("sensors_data");
    var sensorValidationObject = sensorValidationObjects[sensor.addr];
    sensorValidationObject["validation_steps"].push(sensor.sensor_data);
    if(sensor.validation_step >= validationStepsArray.length){
        sensorValidationObject["test_engineer"] = flow.get("user");
        sensorValidationObject["order_number"] = flow.get("order");
        sensorValidationObject["address"] = sensor.addr;
        sensorValidationObject["test_date"] = new Date(sensor.received);
        sensor.validated = true;
        msg.payload = sensor;
        var validate = {};
        validate.payload = JSON.stringify(sensorValidationObject);
        validate.address = sensor.addr;
        validate.test_engineer = flow.get("user");
        return [msg,validate];
    }else{
        msg.payload = sensor;
        return [msg,null];
    }

}

