var PTOUCH_ESC_P_MODE = Buffer.from([0x1b, 0x69, 0x61, 0x00]);
var INITIALIZE = Buffer.from([0x1b,0x40]);
var LARGE_QR = Buffer.from([0x1b,0x69,0x51,0x0C,0x02,0x00,0x00,0x00,0x00,0x02,0x00]);
var AUTO_LENGTH = Buffer.from([0x1b,0x69,0x6c,0x00,0x00]);
var LEFT_MARGIN = Buffer.from([0x1b, 0x24, 0x3C, 0x00]);
var FONT = Buffer.from([0x1B, 0x6B, 0x00]);
var FONT_SIZE = Buffer.from([0x1B, 0x58, 0x34]);

var payload = [];
payload.push(PTOUCH_ESC_P_MODE); //Command to put printer in ESC P command mode
payload.push(INITIALIZE); //Command to initialize printer for commands

//QR Code Bytes
payload.push(LARGE_QR); //QR code config bytes 12 dots per per cell, Model 2, auto input
payload.push(Buffer.from(msg.address+"\\\\\\", "utf-8")); //Payload to embed in QR Code.  Note requires three \ to end QR payload

//Text bytes
payload.push(AUTO_LENGTH); //Auto set length of label
payload.push(LEFT_MARGIN); //1" left margin to allow for QR code on left side of label
payload.push(FONT); //Helsinki font
payload.push(FONT_SIZE); //12pt font size
payload.push(Buffer.from(msg.address.substring(0,11)+"\n", "utf-8"));  //print first half of address on top line
payload.push(LEFT_MARGIN); //1" left margin to allow for QR code on left side of label
payload.push(Buffer.from(msg.address.substring(12,23)+"\n", "utf-8"));  //print second half of address on second line
payload.push(LEFT_MARGIN); //1" left margin to allow for QR code on left side of label
payload.push(Buffer.from("QC: "+msg.test_engineer, "utf-8")); //print quality control engineer on third line
payload.push(Buffer.from([0x0c])); //execute print command

// console.log("First Line: "+msg.payload.address.substring(0,11) +"second line:"+ msg.payload.address.substring(12,23)+"third line:"+msg.payload.test_engineer);

msg.payload = Buffer.concat(payload);

return msg;
